UserDetails实现类

@Data
public class SecurityUserDetails implements UserDetails {

    // 用户
    private transient SysUser sysUser;

    //角色
    private transient List roleList;


    public SecurityUserDetails() {}

    public SecurityUserDetails(SysUser user) {
        if (ObjectUtils.isNotEmpty(user)) {
            this.sysUser = user;
        }
    }

    public SecurityUserDetails(SysUser user, List roleList) {
       if (ObjectUtils.isNotEmpty(user)) {
           this.sysUser = user;
           this.roleList = roleList;
       }
    }

    /**
     * 筛选出当前用户拥有的角色名称：例如admin、super...
     */
    @Override
    public Collection getAuthorities() {
        List authorities = Lists.newArrayList();
        Optional.ofNullable(this.roleList).ifPresent(roles -> {
            roles.forEach(role -> {
                authorities.add(new SimpleGrantedAuthority(role.getRoleName()));
            });
        });
        return authorities;
    }

    // 下面的获取用户、密码、账号是否过期、是否锁定、凭证是否过期、是否有用的这些凭证需要根据实际的需求来配置

    @Override
    public String getPassword() {
        return sysUser.getPassword();
    }

    @Override
    public String getUsername() {
        return sysUser.getUsername();
    }

    @Override
    public boolean isAccountNonExpired() {
        return true;
    }

    @Override
    public boolean isAccountNonLocked() {
        return true;
    }

    @Override
    public boolean isCredentialsNonExpired() {
        return true;
    }


    /**
     * 账号是否启用
     */
    @Override
    public boolean isEnabled() {
        return !sysUser.getIsDeleted();
    }
}

UserDetailsService实现类

@Service("userDetailsService")
public class SecurityUserDetailsService implements UserDetailsService {

    @Resource
    private SysUserMapper sysUserMapper;

    @Resource
    private SysUserRoleMapper sysUserRoleMapper;

    @Resource
    private SysRoleMapper sysRoleMapper;

    /**
     * 根据用户名获取账号的信息
     *
     * @param username
     * @return
     */
    @Override
    public SecurityUserDetails loadUserByUsername(String username) throws UsernameNotFoundException {

        SysUser sysUser = sysUserMapper.selectOne(new QueryWrapper().eq("user_name", username).eq("is_deleted", false));
        Optional.ofNullable(sysUser).orElseThrow(() -> new BizException("用户名不存在"));
        return new SecurityUserDetails(sysUser, getRolesByUserId(sysUser.getId()));
    }


    /**
     * 根据userId获取用户角色权限信息
     *
     * @param userId
     * @return
     */
    private List getRolesByUserId(Long userId) {
        List roles = Lists.newArrayList();
        Optional.ofNullable(sysUserRoleMapper.selectList(new QueryWrapper().eq("user_id", userId).eq("is_deleted", false))).ifPresent(s -> {
            s.stream().filter(sysUserRole -> !sysUserRole.getIsDeleted()).map(SysUserRole::getRoleId).collect(Collectors.toList()).forEach(r -> {
                    roles.add(sysRoleMapper.selectById(r));
                });
            });
        return roles;
    }
}

